}

class NavLinkButton extends React.Component{
  constructor(props){
    super(props);
    this.state ={
      buttonID: 'No Name',
      isSelected: false,
    }
  }



  render(){
    return(
      <div className="NavButton">
        <button className={this.state.buttonID}>
          {this.props.value}
        </button>
      </div>
    );
  }
}
